mapper.select:
SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM ,FN_CONSULTENTNAME(CON_ID) CON_NM ,CON_ID ,IP ,REG_DATE ,PASS_YN FROM I_LOGIN_INFO A, (SELECT @ROWNUM := 0) TMP
CON_ID = #{conId, jdbcType=VARCHAR}
AND DATE_FORMAT(REG_DATE, "%Y-%m-%d") BETWEEN #{startDate} AND #{endDate}
ORDER BY ROWNUM DESC LIMIT #{startRowNum, javaType=INT}, 15

        SELECT
        	COUNT(*)
		FROM
			I_LOGIN_INFO


				CON_ID = #{conId, jdbcType=VARCHAR}


		    	AND DATE_FORMAT(REG_DATE, "%Y-%m-%d") BETWEEN #{startDate} AND #{endDate}
