mapper.select:
SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM ,BOD_ID ,BOD_TYPE ,TITLE ,(SELECT CON_NM FROM I_CONSULTANT WHERE CON_ID = A.CON_ID) AS CON_NM ,CASE WHEN B.ATT_ID IS NULL THEN '0' WHEN B.ATT_ID IS NOT NULL THEN '1' END AS ATT_CNT ,DATE_FORMAT(A.REG_DATE, "%Y-%m-%d") AS REG_DATE FROM I_BOARD A LEFT JOIN I_ATTACH B ON (BOD_ID = ORG_ID AND B.DEL_DATE IS NULL) ,(SELECT @ROWNUM := 0) TMP WHERE A.DEL_DATE IS NULL AND BOD_TYPE = #{bodType, jdbcType=CHAR}
AND (TITLE LIKE CONCAT('%', #{searchText, jdbcType=VARCHAR}, '%') OR CONTENTS LIKE CONCAT('%', #{searchText, jdbcType=VARCHAR}, '%'))
ORDER BY A.REG_DATE DESC LIMIT #{startRowNum, javaType=INT}, 15

mapper.insert:

    SELECT CONCAT("BOD", LPAD(RIGHT(IFNULL(MAX(BOD_ID), 0), 8)+1, 8, 0)) FROM I_BOARD

INSERT INTO I_BOARD
(
    BOD_ID
    ,BOD_TYPE
    ,TITLE
    ,CONTENTS
    ,CON_ID
    ,REG_DATE
)
VALUES
(
    #{bodId, jdbcType=VARCHAR}
    ,#{bodType, jdbcType=VARCHAR}
    ,#{title, jdbcType=VARCHAR}
    ,#{contents, jdbcType=VARCHAR}
    ,#{conId, jdbcType=VARCHAR}
    ,NOW()
)

mapper.update:
	UPDATE I_BOARD
	SET
        TITLE = #{title, jdbcType=VARCHAR}
        ,CONTENTS = #{contents, jdbcType=VARCHAR}
	WHERE
		BOD_ID = #{bodId, jdbcType=VARCHAR}
